package com.test01;

public class Operator05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Operator05 op= new Operator05();
		
		op.opTest();
		
	}
	
	public void opTest() {
		
		int res, num=12;
		
		System.out.println(num);
		
		num +=3;
		System.out.println(num);
		
		num -=5;
		System.out.println(num);
		
		num /=2;
		System.out.println(num);
		
		num *=2;
		System.out.println(num);
	}
	
}
